import React, {Component} from 'react';
import OverHeadBar from "./OverHeadBar";
import detail from "./DetailPage.module.css";

class DetailPage extends Component{
    state = {
        loading: true,
        product: null
    };

    async componentDidMount() {
        const response = await fetch(`/api/products/${this.props.match.params.id}`);
        const product = await response.json();
        this.setState({loading: false, product: product})
        console.log(this.props);
    }

    render() {
        /*const {picture} = this.state.product;*/
        return (
            <div>
                <OverHeadBar currentPage='SHOP'/>
                <div className={detail.overview}>
                    {/*<img className={detail.pic} src={`assets/${picture}`} />*/}
                    <div className={detail.text}>
                        <div className={detail.name}>

                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default DetailPage;
