import React, {Component} from 'react';
import merch from "../views/MerchPage.module.css";
import Tag from "./Tag";

class Product extends Component{
    render() {
        console.log(`../assets/${this.props.product.picture}`);
        return (
            <div>
                <div className={merch.tile}>
                    <img className={merch.pic}
                         src={"../assets/Tshirt1-square.jpg"}
                         alt={this.props.product.description}/>
                    <Tag name={this.props.product.name}
                         description={this.props.product.description}
                         price={`£ ${this.props.product.price}`} />
                </div>
                {this.props.product.id} {this.props.product.name}
            </div>
        );
    }
}

export default Product;

