import React, {Component} from "react";
import {OutlinedButton} from "../views/ButtonStyle";
import home from "../views/HomePage.module.css";

class EmailForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            userEmail: '',
        }
    }

    handleUserEmailChange = (event) => {
        this.setState({
            userEmail: event.target.value
        })
    };

    handleSubmit = (event) => {
        const text = this.state.userEmail;
        event.preventDefault();
        alert(`Success: ${text} added`);

        if (this.state.userEmail) {
            fetch(('/api/subs'), {
                mode: 'no-cors',
                method: 'POST',
                cache: 'no-cache',
                headers: {
                    'Content-Type': 'application/x-www-form-urlencoded'
                },
                body: JSON.stringify({
                    email: text
                })
            }).then((res) => {
                console.log(res);
                res.json();
            })
            .catch(err => console.log(err));

        }
    };

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <div className={home.email}>
                    <input className={home.subscribe}
                        type='email'
                        name='userEmail'
                        placeholder='Enter Email Address'
                        value={this.state.userEmail}
                        onChange={this.handleUserEmailChange}/>
                    <OutlinedButton
                        type='submit'
                        style={{fontSize: '2vh', padding: '0 2vh', height: 40}}>
                        Subscribe!
                    </OutlinedButton>
                </div>
            </form>
        )
    }
}

export default EmailForm;
